mapper.sql:
create table if not exists user (
    `id` int primary key auto_increment,
    `name` varchar(30),
    `basicCostId` int,
    `phoneNumber` varchar(15)
);

create table if not exists `usage` (
    `userId` int,
    `amount` float,
    `startTime` datetime,
    `serviceType` enum('CALL', 'SMS', 'LOCAL_DATA', 'DOMESTIC_DATA')
);
